export const generateLayerColors = (layers, baseColor = "var(--bg-support-aldebaran)") => {
    const colors = {};
    if (layers.length === 0) {
        return colors;
    }
    if (layers.length === 1) {
        colors[layers[0]] = baseColor;
        return colors;
    }
    layers.forEach((layer, index) => {
        const ratio = index / (layers.length - 1);
        if (ratio === 0) {
            colors[layer] = `color-mix(in srgb, ${baseColor} 60%, white)`;
        }
        else if (ratio === 1) {
            colors[layer] = `color-mix(in srgb, ${baseColor} 80%, black)`;
        }
        else {
            const lightRatio = 60 + 20 * (1 - ratio);
            const darkRatio = 20 * ratio;
            colors[layer] =
                `color-mix(in srgb, color-mix(in srgb, ${baseColor} ${lightRatio}%, white) ${100 - darkRatio}%, black)`;
        }
    });
    return colors;
};
export const resolveCSSVarColor = (value, el = document.documentElement) => {
    if (value.startsWith("var(")) {
        const match = value.match(/var\((--[^),\s]+)/);
        if (match) {
            const cssVarName = match[1];
            const resolved = getComputedStyle(el).getPropertyValue(cssVarName).trim();
            return resolved || value;
        }
    }
    return value;
};
